package com.enonic.wem.uitest.contentimport com.enonic.autotests.pages.contentmanager.browsepanel.ContentBrowsePanelimport com.enonic.autotests.pages.contentmanager.browsepanel.NewContentDialogimport com.enonic.autotests.services.NavigatorHelperimport com.enonic.wem.uitest.BaseGebSpecimport spock.lang.Sharedclass NewContentDialogSpec    extends BaseGebSpec{    @Shared    ContentBrowsePanel contentBrowsePanel;    def setup()    {        go "admin"        //String baseUrl = getTestSession().getBaseUrl();        //getDriver().navigate().to( baseUrl + "admin/" );        contentBrowsePanel = NavigatorHelper.openContentApp( getTestSession() );    }    def "GIVEN content App BrowsePanel WHEN New button clicked THEN 'new-content' dialog with title 'What do you want to create' showed"()    {        when:        NewContentDialog newContentDialog = contentBrowsePanel.clickToolbarNew();        then:        newContentDialog.isOpened();    }    def "GIVEN opened a 'new-content' dialog WHEN adding text-search 'folder'  THEN only one content type showed in the list "()    {        given:        NewContentDialog newContentDialog = contentBrowsePanel.clickToolbarNew();        when:        newContentDialog.typeSearchText( "folder" );        then:        newContentDialog.getNumberContentTypesFromList() == 1;    }    def "GIVEN opened a 'new-content' dialog, WHEN adding text-search 'site'  THEN only one content type showed in the list "()    {        given:        NewContentDialog newContentDialog = contentBrowsePanel.clickToolbarNew();        when:        newContentDialog.typeSearchText( "site" );        then:        newContentDialog.getNumberContentTypesFromList() == 1;    }    def "GIVEN opened a 'new-content' dialog, WHEN adding text-search 'shortcut'  THEN only one content type showed in the list "()    {        given:        NewContentDialog newContentDialog = contentBrowsePanel.clickToolbarNew();        when:        newContentDialog.typeSearchText( "shortcut" );        then:        newContentDialog.getNumberContentTypesFromList() == 1;    }    def "GIVEN opened a 'new-content' dialog and added text-search 'unstructured'  WHEN search input cleared THEN initial list of content types showed"()    {        given:        NewContentDialog newContentDialog = contentBrowsePanel.clickToolbarNew();        int number = newContentDialog.getNumberContentTypesFromList();        newContentDialog.typeSearchText( "unstructured" );        when:        newContentDialog.clearSearchInput();        then:        newContentDialog.getNumberContentTypesFromList() == number;    }}