package com.enonic.wem.uitest.contentimport com.enonic.autotests.pages.contentmanager.browsepanel.ContentBrowsePanelimport com.enonic.autotests.pages.contentmanager.browsepanel.NewContentDialogimport com.enonic.autotests.services.NavigatorHelperimport com.enonic.wem.uitest.BaseGebSpecimport spock.lang.Ignoreimport spock.lang.Sharedclass NewContentDialogSpec    extends BaseGebSpec{    @Shared    ContentBrowsePanel contentBrowsePanel;    def setup()    {        go "admin"        contentBrowsePanel = NavigatorHelper.openContentApp( getTestSession() );    }    def "GIVEN content App BrowsePanel WHEN New button clicked THEN 'new-content' dialog with title 'Create Content' showed"()    {        when: "New button on toolbar pressed"        NewContentDialog newContentDialog = contentBrowsePanel.clickToolbarNew();        then: "dialog with title 'Create Content' shown"        newContentDialog.isOpened();        and: "search input is enabled"        newContentDialog.isSearchInputEnabled();        and: "upload button enabled"        newContentDialog.isUploadButtonEnabled();    }    def "GIVEN opened a 'new-content' dialog WHEN adding text-search 'folder'  THEN only one content type showed in the list "()    {        given: "opened a 'new-content' dialog"        NewContentDialog newContentDialog = contentBrowsePanel.clickToolbarNew();        when: "'folder' search text typed"        newContentDialog.typeSearchText( "folder" );        then: "only one item shown"        newContentDialog.getNumberContentTypesFromList() == 1;    }    def "GIVEN opened a 'new-content' dialog WHEN adding text-search not existing type  THEN empty list should be shown "()    {        given: "opened a 'new-content' dialog"        NewContentDialog newContentDialog = contentBrowsePanel.clickToolbarNew();        when: "wrong search text typed"        newContentDialog.typeSearchText( "supertype" );        then: "empty list shown"        newContentDialog.getNumberContentTypesFromList() == 0;    }    def "GIVEN opened a 'new-content' dialog, WHEN adding text-search 'site'  THEN only one content type showed in the list "()    {        given: "opened a 'new-content' dialog"        NewContentDialog newContentDialog = contentBrowsePanel.clickToolbarNew();        when: "'site' search text typed"        newContentDialog.typeSearchText( "site" );        then: then: "only one item shown"        newContentDialog.getNumberContentTypesFromList() == 1;    }    def "GIVEN opened a 'new-content' dialog, WHEN adding text-search 'shortcut'  THEN only one content type showed in the list "()    {        given: "opened a 'new-content' dialog"        NewContentDialog newContentDialog = contentBrowsePanel.clickToolbarNew();        when: "'shortcut' search text typed"        newContentDialog.typeSearchText( "shortcut" );        then: newContentDialog.getNumberContentTypesFromList() == 1;    }    def "GIVEN opened a 'new-content' dialog and added text-search 'unstructured'  WHEN search input cleared THEN initial list of content types showed"()    {        given: "opened a 'new-content' dialog and 'unstructured' text typed"        NewContentDialog newContentDialog = contentBrowsePanel.clickToolbarNew();        int number = newContentDialog.getNumberContentTypesFromList();        newContentDialog.typeSearchText( "unstructured" );        when: "search input cleared"        newContentDialog.clearSearchInput();        then: "initial list of content types showed"        newContentDialog.getNumberContentTypesFromList() == number;    }}