package com.enonic.wem.uitest.contentimport com.enonic.autotests.pages.contentmanager.browsepanel.ContentBrowsePanelimport com.enonic.autotests.pages.contentmanager.browsepanel.NewContentDialogimport com.enonic.autotests.services.NavigatorHelperimport com.enonic.wem.uitest.BaseGebSpecimport spock.lang.Sharedclass NewContentDialogSpec    extends BaseGebSpec{    @Shared    ContentBrowsePanel contentBrowsePanel;    def setup()    {        go "admin"        contentBrowsePanel = NavigatorHelper.openContentApp( getTestSession() );    }    def "GIVEN content App BrowsePanel WHEN New button clicked THEN 'new-content' dialog with title 'What do you want to create' showed"()    {        when:        NewContentDialog newContentDialog = contentBrowsePanel.clickToolbarNew();        then:        newContentDialog.isOpened();    }    def "GIVEN opened a 'new-content' dialog WHEN adding text-search 'archive' , the name of a content type THEN only one content type showed in the list "()    {        given:        NewContentDialog newContentDialog = contentBrowsePanel.clickToolbarNew();        when:        newContentDialog.typeSearchText( "archive" );        then:        newContentDialog.getNumberContentTypesFromList() == 1;    }    def "GIVEN opened a 'new-content' dialog, WHEN adding text-search 'HTML Area' , the display-name of a content type THEN only one content type showed in the list "()    {        given:        NewContentDialog newContentDialog = contentBrowsePanel.clickToolbarNew();        when:        newContentDialog.typeSearchText( "HTML Area" );        then:        newContentDialog.getNumberContentTypesFromList() == 1;    }    def "GIVEN opened a 'new-content' dialog and added text-search 'archive'  WHEN search input cleared THEN initial list of content types showed"()    {        given:        NewContentDialog newContentDialog = contentBrowsePanel.clickToolbarNew();        int number = newContentDialog.getNumberContentTypesFromList();        newContentDialog.typeSearchText( "archive" );        when:        newContentDialog.clearSearchInput();        then:        newContentDialog.getNumberContentTypesFromList() == number;    }}